import React from 'react';

function Cart({ cart, removeFromCart, emptyCart }) {
  // This will calculate the total price and handle possible errors
  const totalPrice = cart.reduce((total, item) => {
    if (typeof item.price === 'number' && typeof item.quantity === 'number') {
      return total + item.price * item.quantity;
    } else {
      console.error('Invalid item in cart:', item);
      return total;
    }
  }, 0);

  // Format the total price with two decimal places
  const formattedTotalPrice = isNaN(totalPrice) ? '0.00' : totalPrice.toFixed(2);

  return (
    <div className="cart-container">
      <h2>Your Cart</h2>
      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <ul className="cart-list"> {/* Use a <ul> for the list container */}
          {cart.map(item => (
            <li key={item.id} className="cart-item"> {/* Each item is a <li> */}
              <span className="item-name">{item.name}</span> {/* Style the name separately if needed */}
              <span>Quantity: {item.quantity}</span>
              <span>Price: ${(item.price * item.quantity).toFixed(2)}</span>
              <button onClick={() => removeFromCart(item.id)}>Remove</button>
              <button onClick={emptyCart}>Empty Cart</button>
            </li>
          ))}
        </ul>
      )}
      <div className="total-price">Total: ${totalPrice.toFixed(2)}</div>
    </div>
  );
}

export default Cart;
