import React, { useState, useEffect } from 'react';
// Import ProductList and Cart components
import ProductList from './ProductList';
import Cart from './cart';

const rainbowOrder = {
  Red: 1,
  Orange: 2,
  Yellow: 3,
  Green: 4,
  Blue: 5,
  Indigo: 6,
  Violet: 7,
  Black: 8,
  Gray: 9,
  White: 10
};

function App() {
  const [inventory, setInventory] = useState([]); // State for inventory
  const [cart, setCart] = useState(() => {
    // Initialize cart from localStorage if available
    const savedCart = localStorage.getItem('cart');
    return savedCart ? JSON.parse(savedCart) : [];
  });
  const [categoryFilter, setCategoryFilter] = useState('all');
  const [sortType, setSortType] = useState('');

  // Effect to fetch inventory from a JSON file
  useEffect(() => {
    fetch('inventory.json') // Adjust the path to your inventory.json file
      .then(response => response.json())
      .then(data => setInventory(data))
      .catch(error => console.error("Failed to load inventory", error));
  }, []);

  // Effect to save the cart to localStorage on changes
  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cart));
  }, [cart]);

  const addToCart = (product) => {
    setCart((prev) => {
      const exist = prev.find((item) => item.id === product.id);
      if (exist) {
        return prev.map((item) =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      }
      return [...prev, { ...product, quantity: 1 }];
    });
  };

  const removeFromCart = productId => {
    setCart(prev => prev.filter(item => item.id !== productId));
  };

  const handleCategoryChange = (event) => {
    setCategoryFilter(event.target.value);
  };

  const handleSortChange = (event) => {
    setSortType(event.target.value);
  };

  const sortInventory = (inv, type) => {
    switch (type) {
      case 'priceLowHigh':
        return [...inv].sort((a, b) => a.price - b.price);
      case 'priceHighLow':
        return [...inv].sort((a, b) => b.price - a.price);
      case 'colorRainbow':
        return [...inv].sort((a, b) => rainbowOrder[a.color] - rainbowOrder[b.color]);
      default:
        return inv;
    }
  };

  const emptyCart = () => {
    setCart([]);
  };

  const filteredInventory = categoryFilter === 'all'
    ? inventory
    : inventory.filter(item => item.category === categoryFilter);

  const sortedInventory = sortInventory(filteredInventory, sortType);

  return (
    <div className="App">
      <div className="filter-section">
        <span>Filter:</span>
        <select onChange={handleCategoryChange} value={categoryFilter}>
          <option value="all">All</option>
          <option value="sweaters">Sweaters</option>
          <option value="hats">Hats</option>
          <option value="socks">Socks</option>
          <option value="shirts">Shirts</option>
        </select>
      </div>

      <div className="sort-section">
        <span>Sort:</span>
        <select onChange={handleSortChange} value={sortType}>
          <option value="">Default</option>
          <option value="priceLowHigh">Price: Low to High</option>
          <option value="priceHighLow">Price: High to Low</option>
          <option value="colorRainbow">Color: Rainbow Order</option>
        </select>
      </div>

      <ProductList addToCart={addToCart} inventory={sortedInventory} />
      <Cart cart={cart} removeFromCart={removeFromCart} emptyCart={emptyCart} />
    </div>
  );
}

export default App;
