import React, { useState } from 'react';
import './App.css';

function ProductList({ addToCart, inventory }) {
  // State to keep track of selected sizes for each product
  const [selectedSizes, setSelectedSizes] = useState({});

  const handleSizeChange = (id, size) => {
    setSelectedSizes(prevSizes => ({
      ...prevSizes,
      [id]: size
    }));
  };

  const handleAddToCart = (product) => {
    // If the product requires a size and it has not been selected, alert the user
    if (product.sizes && !selectedSizes[product.id]) {
      alert('Please select a size');
      return;
    }

    addToCart({
      ...product,
      size: selectedSizes[product.id] // Include the selected size if applicable
    });
  };

  return (
    <div className="products-container">
      {inventory.map((product) => (
        <div key={product.id} className="product">
          <img src={product.image} alt={product.name} />
          <h3>{product.name}</h3>
          <p>${product.price.toFixed(2)}</p>

          {/* Render size dropdown if sizes are available */}
          {product.sizes && (
            <select
              onChange={(e) => handleSizeChange(product.id, e.target.value)}
              value={selectedSizes[product.id] || ''}
            >
              <option value="">Select Size</option>
              {product.sizes.map(size => (
                <option key={size} value={size}>{size}</option>
              ))}
            </select>
          )}

          <button onClick={() => handleAddToCart(product)}>Add to Cart</button>
        </div>
      ))}
    </div>
  );
}

export default ProductList;
