import React from 'react';

function Cart({ cart, removeFromCart }) {
  // This will calculate the total price and handle possible errors
  const totalPrice = cart.reduce((total, item) => {
    if (typeof item.price === 'number' && typeof item.quantity === 'number') {
      return total + item.price * item.quantity;
    } else {
      console.error('Invalid item in cart:', item);
      return total;
    }
  }, 0);

  // Format the total price with two decimal places
  const formattedTotalPrice = isNaN(totalPrice) ? '0.00' : totalPrice.toFixed(2);

  return (
    <div className="cart-container">
      <h2>Your Cart</h2>
      <div>
        {cart.length === 0 ? (
          <p>Your cart is empty.</p>
        ) : (
          cart.map(item => (
            <div key={item.id} className="cart-item">
              <span>{item.name}</span> {/* Item name */}
              <span>Quantity: {item.quantity}</span> {/* Item quantity */}
              <span>Price: ${(item.price * item.quantity).toFixed(2)}</span> {/* Item price */}
              <button onClick={() => removeFromCart(item.id)}>Remove</button> {/* Remove button */}
            </div>
          ))
        )}
      </div>
      <div className="total-price">Total: ${formattedTotalPrice}</div>
    </div>
  );
}

export default Cart;
