import React from 'react';


  function Cart({ cart, removeFromCart }) {
    console.log('Cart before total calculation:', cart);

    // This will calculate the total price and handle possible errors
    const totalPriceCalc = cart.reduce((total, item) => {
      console.log('Processing item:', item);
      if (typeof item.price !== 'number' || typeof item.quantity !== 'number') {
        console.error('Invalid item in cart:', item);
        return total;
      }
      return total + (item.price * item.quantity);
    }, 0);

    console.log('Total price:', totalPriceCalc);
  const totalPrice = cart.reduce((total, item) => {
    if (typeof item.price === 'number' && typeof item.quantity === 'number') {
      return total + item.price * item.quantity;
    } else {
      // Handle the error appropriately
      console.error('Item price or quantity is not a number', item);
      return total;
    }
  }, 0);

  return (
    <div className="cart-container">
      <h2>Your Cart</h2>
      <div>
        {cart.length === 0 ? (
          <p>Your cart is empty.</p>
        ) : (
          cart.map(item => (
            <div key={item.id} className="cart-item">
              <span>{item.name}</span> {/* Item name */}
              <span>Quantity: {item.quantity}</span> {/* Item quantity */}
              <span>Price: ${item.price.toFixed(2)}</span> {/* Item price */}
              <button onClick={() => removeFromCart(item.id)}>Remove</button> {/* Remove button */}
            </div>
          ))
        )}
      </div>
      <div className="total-price">Total: ${totalPrice.toFixed(2)}</div>
    </div>
  );
}

export default Cart;
