import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import ProductList from './ProductList';
import Cart from './cart';
import HomePage from './HomePage';
import Header from './Header';

const rainbowOrder = {
  Red: 1,
  Orange: 2,
  Yellow: 3,
  Green: 4,
  Blue: 5,
  Black: 8,
  Gray: 9,
};

const ProductsPage = ({ cart, addToCart, inventory, removeFromCart, emptyCart, isCartVisible, toggleCart, categoryFilter, handleCategoryChange, sortType, handleSortChange, sortedInventory }) => {
  return (
    <div className="App">
      <Header />
      <Link to="/">Go to Home Page</Link>
      </div>
  );
};

function App() {
  const [inventory, setInventory] = useState([]);
  const [cart, setCart] = useState(() => {
    const savedCart = localStorage.getItem('cart');
    return savedCart ? JSON.parse(savedCart) : [];
  });
  const [categoryFilter, setCategoryFilter] = useState('all');
  const [sortType, setSortType] = useState('');
  const [isCartVisible, setIsCartVisible] = useState(false);

  const toggleCart = () => setIsCartVisible(!isCartVisible);

  useEffect(() => {
    fetch('inventory.json')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => setInventory(data))
      .catch(error => console.error("Failed to load inventory", error));
  }, []);

  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cart));
  }, [cart]);

  const addToCart = (product) => {
    setCart((prev) => {
      const exist = prev.find((item) => item.id === product.id);
      if (exist) {
        return prev.map((item) =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      }
      return [...prev, { ...product, quantity: 1 }];
    });
  };

  const removeFromCart = productId => {
    setCart(prev => prev.filter(item => item.id !== productId));
  };

  const handleCategoryChange = (event) => {
    setCategoryFilter(event.target.value);
  };

  const handleSortChange = (event) => {
    setSortType(event.target.value);
  };

  const sortInventory = (inv, type) => {
    switch (type) {
      case 'priceLowHigh':
        return [...inv].sort((a, b) => a.price - b.price);
      case 'priceHighLow':
        return [...inv].sort((a, b) => b.price - a.price);
      case 'colorRainbow':
        return [...inv].sort((a, b) => rainbowOrder[a.color] - rainbowOrder[b.color]);
      default:
        return inv;
    }
  };

  const emptyCart = () => {
    setCart([]);
  };

  const filteredInventory = categoryFilter === 'all'
    ? inventory
    : inventory.filter(item => item.category === categoryFilter);

  const sortedInventory = sortInventory(filteredInventory, sortType);

  const productsPageProps = {
    cart,
    addToCart,
    inventory,
    removeFromCart,
    emptyCart,
    isCartVisible,
    toggleCart,
    categoryFilter,
    handleCategoryChange,
    sortType,
    handleSortChange,
    sortedInventory
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/products" element={<ProductsPage {...productsPageProps} />} />
      </Routes>
    </Router>
  );
}

export default App;
