import React, { useState, useEffect } from 'react';
import inventory from './inventory';
import ProductList from './ProductList';
import Cart from './cart';

const rainbowOrder = {
  Red: 1,
  Orange: 2,
  Yellow: 3,
  Green: 4,
  Blue: 5,
  Indigo: 6,
  Violet: 7,
  Black: 8,
  White: 9,
  Gray: 10
};

function App() {
  const [cart, setCart] = useState([]);
  const [categoryFilter, setCategoryFilter] = useState('all');
  const [sortType, setSortType] = useState('');
  const [sortedInventory, setSortedInventory] = useState([...inventory]);

  useEffect(() => {
    let sortedItems = [...inventory];
    if (categoryFilter !== 'all') {
      sortedItems = sortedItems.filter(item => item.category === categoryFilter);
    }

    switch (sortType) {
      case 'priceLowHigh':
        sortedItems.sort((a, b) => a.price - b.price);
        break;
      case 'priceHighLow':
        sortedItems.sort((a, b) => b.price - a.price);
        break;
      case 'colorRainbow':
        sortedItems.sort((a, b) => rainbowOrder[a.color] - rainbowOrder[b.color]);
        break;
      default:
        // You can decide if you want to apply a default sort order
        break;
    }

    setSortedInventory(sortedItems);
  }, [categoryFilter, sortType]); // Only re-run the effect if categoryFilter or sortType changes

  const addToCart = (product) => {
    setCart((prev) => {
      const exist = prev.find((item) => item.id === product.id);
      if (exist) {
        return prev.map((item) =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      }
      return [...prev, { ...product, quantity: 1 }];
    });
  };

  const removeFromCart = productId => {
    setCart(prev => prev.filter(item => item.id !== productId));
  };

  const handleCategoryChange = (event) => {
    setCategoryFilter(event.target.value);
  };

  const handleSortChange = (event) => {
    setSortType(event.target.value);
  };

  const emptyCart = () => {
    setCart([]);
  };

  return (
    <div className="App">
      <select onChange={handleCategoryChange}>
        <option value="all">All</option>
        <option value="sweaters">Sweaters</option>
        <option value="hats">Hats</option>
        <option value="socks">Socks</option>
        <option value="shirts">Shirts</option>
      </select>

      <select onChange={handleSortChange}>
        <option value="">Select sort type</option>
        <option value="priceLowHigh">Price: Low to High</option>
        <option value="priceHighLow">Price: High to Low</option>
        <option value="colorRainbow">Color: Rainbow</option>
      </select>

      <ProductList addToCart={addToCart} inventory={sortedInventory} />
      <Cart cart={cart} removeFromCart={removeFromCart} emptyCart={emptyCart} />
    </div>
  );
}

export default App;
